View.ClientEvent:
window.showChd = function(id){
	view.get("#dsChd").set("parameter",{
		id:id
	}).flushAsync();
	view.get("#dialogChd").show();
}

window.flushDd = function(){
	view.get("#dsDd").flushAsync();
}

DataSet.ClientEvent:
var now = new Date();
var y = now.getFullYear();
var m = now.getMonth();
var sjq = now.setFullYear(y,m,1);
var sjz = now.setFullYear(y,m+1,1);
sjz= new Date((sjz/1000-86400)*1000)

self.insert();
