Button.ClientEvent:
var currentData = view.get("#dsCg").getData("#");
var dsDdxx = view.get("#dsCgxx");
if(currentData != undefined){
	dorado.MessageBox.confirm("是否将当前订单删除？",function(){
	var id = currentData.get("id");
	view.get("#ajaxActionCgzt").set("parameter",{lx:"N",id:id}).execute();
	view.get("#dsCg").flushAsync();
	});	
}

var currentData = view.get("#dsCg").getData("#");
var dsDdxx = view.get("#dsCgxx");
if(currentData != undefined){
	dorado.MessageBox.confirm("是否将当前订单设为已完成？",function(){
	var id = currentData.get("id");
	view.get("#ajaxActionCgzt").set("parameter",{lx:"G",id:id}).execute();
	view.get("#dsCg").flushAsync();
	});	
}

var currentData = view.get("#dsCg").getData("#");
var dsDdxx = view.get("#dsCgxx");
if(currentData != undefined){
	dorado.MessageBox.confirm("是否取消当前订单归档状态？",function(){
	var id = currentData.get("id");
	view.get("#ajaxActionCgzt").set("parameter",{lx:"Y",id:id}).execute();
	view.get("#dsCg").flushAsync();
	});	
}

