DataSet.ClientEvent:
var now = new Date();
var y = now.getFullYear();
var m = now.getMonth();
var sjq = now.setFullYear(y,m-1,1);
var sjz = now.setFullYear(y,m,1);
sjz= new Date((sjz/1000-86400)*1000)

self.insert({
	sjq:sjq,
	sjz:sjz
});

Button.ClientEvent:
var action = view.get("#export2ReportActionResult");

action.doDowloadReport = function(data) {
		var id = data.id;
		var name = data.name;
		if (id && name) {
			var url = $url(">dorado/bdf2/export/doDownloadFile?id=" + id + "&name=" + name);
			window.open(url, "_blank");
		}
};
if(view.get("#dsKqhz.data:#")){
	action.execute();
}

